/// <reference types="vite/client" />

// SVG imports
declare module '*.svg' {
	const content: string
	export default content
}

// Other asset types that Vite handles
declare module '*.png' {
	const content: string
	export default content
}

declare module '*.jpg' {
	const content: string
	export default content
}

declare module '*.jpeg' {
	const content: string
	export default content
}

declare module '*.gif' {
	const content: string
	export default content
}

declare module '*.webp' {
	const content: string
	export default content
}

declare module '*.ico' {
	const content: string
	export default content
}
